import React, { useState, useEffect } from "react";
import "./style.css";
import { GrFormNextLink } from "react-icons/gr";
// import moment, { tz } from "moment";
// import "moment-timezone";
const CustomInput = () => {
  const [indianTime, setIndianTime] = useState("");
  const [hongKongTime, setHongKongTime] = useState("");

  const convertTime = () => {
    const [hours, minutes] = indianTime.split(":");
    const indianDate = new Date();
    indianDate.setHours(parseInt(hours, 10));
    indianDate.setMinutes(parseInt(minutes, 10));
    indianDate.setSeconds(0);

    const indianOffset = indianDate.getTimezoneOffset() * 60000; // Convert minutes to milliseconds
    const hongKongOffset = -480 * 60000; // Hong Kong is UTC+8 (480 minutes)
    const hongKongTimes = new Date(
      indianDate.getTime() + indianOffset + hongKongOffset
    );

    setHongKongTime(hongKongTimes.toISOString().slice(0, 19).replace("T", " "));
    console.log(hongKongTime);
  };

  const [hours, setHours] = useState("");
  const [minutes, setMinutes] = useState("");
  const [seconds, setSeconds] = useState("");
  const [amPm, setAmPm] = useState("AM");

  const handleTimeSubmit = () => {
    const data = {
      hours: parseInt(hours),
      minutes: parseInt(minutes),
      seconds: parseInt(seconds),
      am_pm: amPm,
    };

    fetch("/api/save_custom_time", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
        // Handle success or display a notification to the user
      })
      .catch((error) => {
        console.error("Error:", error);
        // Handle error or display a notification to the user
      });
  };

  return (
    <div className="input-wrapper-container">
      <div className="input-wrapper">
        <input
          placeholder="Custom Time"
          type="time"
          id="indianTime"
          value={indianTime}
          onChange={(e) => setIndianTime(e.target.value)}
        />

        <GrFormNextLink size={30} cursor={"pointer"} onClick={convertTime} />
      </div>
    </div>
  );
};

export default CustomInput;
