import React, { useState } from "react";
import "./style.css";
import { GrFormNextLink } from "react-icons/gr";

const CustomInput = () => {
  const [hours, setHours] = useState("");
  const [minutes, setMinutes] = useState("");
  const [seconds, setSeconds] = useState("");
  const [amPm, setAmPm] = useState("AM");

  const [inputTime, setInputTime] = useState("");
  const [hongKongTime, setHongKongTime] = useState("");
  const [usTime, setUsTime] = useState("");

  const handleInputChange = (e) => {
    const inputValue = e.target.value;
    setInputTime(inputValue);

    const options = {
      hour: "numeric",
      minute: "numeric",
      hour12: true,
      timeZone: "Asia/Hong_Kong",
    };
    const hongKongFormattedTime = new Intl.DateTimeFormat(
      "en-US",
      options
    ).format(new Date(`2000-01-01T${inputValue}`));
    setHongKongTime(hongKongFormattedTime);

    options.timeZone = "America/New_York";
    const usFormattedTime = new Intl.DateTimeFormat("en-US", options).format(
      new Date(`2000-01-01T${inputValue}`)
    );
    setUsTime(usFormattedTime);
  };

  const handleTimeSubmit = () => {
    const data = {
      hours: parseInt(hours),
      minutes: parseInt(minutes),
      seconds: parseInt(seconds),
      am_pm: amPm,
    };

    fetch("/api/save_custom_time", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
        // Handle success or display a notification to the user
      })
      .catch((error) => {
        console.error("Error:", error);
        // Handle error or display a notification to the user
      });
  };

  return (
    <div className="input-wrapper-container">
      <div className="input-wrapper">
        <input
          placeholder="Custom Time"
          type="text"
          id="indianTime"
          value={inputTime}
          onChange={handleInputChange}
        />

        <GrFormNextLink size={30} cursor={"pointer"} />
      </div>
    </div>
  );
};

export default CustomInput;
