import React, { useState, useEffect } from "react";
import moment from "moment-timezone";

const IndianToHongKongTimeConverter = () => {
  const [indianHours, setIndianHours] = useState("");
  const [indianMinutes, setIndianMinutes] = useState("");
  const [indianSeconds, setIndianSeconds] = useState("");
  const [convertedTime, setConvertedTime] = useState("");

  useEffect(() => {
    const timer = setInterval(() => {
      if (indianHours && indianMinutes && indianSeconds) {
        const indianTime = moment({
          hours: parseInt(indianHours, 10),
          minutes: parseInt(indianMinutes, 10),
          seconds: parseInt(indianSeconds, 10),
        }).tz("Asia/Kolkata");

        const hongKongTime = indianTime.clone().tz("Asia/Hong_Kong");
        const formattedTime = hongKongTime.format("hh:mm:ss A");
        setConvertedTime(formattedTime);
      }
    }, 1000);

    return () => {
      clearInterval(timer);
    };
  }, [indianHours, indianMinutes, indianSeconds]);

  const convertTime = () => {
    if (indianHours && indianMinutes && indianSeconds) {
      const indianTime = moment({
        hours: parseInt(indianHours, 10),
        minutes: parseInt(indianMinutes, 10),
        seconds: parseInt(indianSeconds, 10),
      }).tz("Asia/Kolkata");

      const hongKongTime = indianTime.clone().tz("Asia/Hong_Kong");
      const formattedTime = hongKongTime.format("hh:mm:ss A");
      setConvertedTime(formattedTime);
    }
  };

  return (
    <div>
      <div>
        <label>Hours:</label>
        <input
          type="number"
          value={indianHours}
          onChange={(e) => setIndianHours(e.target.value)}
        />
      </div>
      <div>
        <label>Minutes:</label>
        <input
          type="number"
          value={indianMinutes}
          onChange={(e) => setIndianMinutes(e.target.value)}
        />
      </div>
      <div>
        <label>Seconds:</label>
        <input
          type="number"
          value={indianSeconds}
          onChange={(e) => setIndianSeconds(e.target.value)}
        />
      </div>
      <button onClick={convertTime}>Convert</button>
      {convertedTime && <div>Digital Time: {convertedTime}</div>}
    </div>
  );
};

export default IndianToHongKongTimeConverter;
