import React, { useEffect, useState } from "react";

const Hong_Us = () => {
  const [hongKongTime, setHongKongTime] = useState("");
  const [usTime, setUsTime] = useState("");

  useEffect(() => {
    const interval = setInterval(() => {
      const now = new Date();
      const hongKongTime = new Date(
        now.toLocaleString("en-US", { timeZone: "Asia/Hong_Kong" })
      ).toLocaleTimeString();
      const usTime = now.toLocaleTimeString("en-US", {
        timeZone: "America/New_York",
      });

      setHongKongTime(hongKongTime);
      setUsTime(usTime);
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="additional-clock-zones">
      <h2>Additional Clock Zones</h2>
      <p>Hong Kong Time: {hongKongTime}</p>
      <p>US Time: {usTime}</p>
    </div>
  );
};

export default Hong_Us;
