import React, { useState } from "react";

const IndianToHongKongTimeConverter = () => {
  const [hours, setHours] = useState("");
  const [minutes, setMinutes] = useState("");
  const [seconds, setSeconds] = useState("");
  const [period, setPeriod] = useState("AM");
  const [convertedTime, setConvertedTime] = useState("");

  const convertTime = () => {
    // Convert hours to 24-hour format
    let convertedHours = parseInt(hours, 10);
    if (period === "PM" && convertedHours !== 12) {
      convertedHours += 12;
    } else if (period === "AM" && convertedHours === 12) {
      convertedHours = 0;
    }

    // Convert to Hong Kong time
    const hongKongHours = (convertedHours + 2) % 24;

    // Format the time
    const convertedPeriod = hongKongHours >= 12 ? "PM" : "AM";
    const convertedHours12 =
      hongKongHours === 0
        ? 12
        : hongKongHours > 12
        ? hongKongHours - 12
        : hongKongHours;
    const formattedTime = `${String(convertedHours12).padStart(
      2,
      "0"
    )}:${minutes.padStart(2, "0")}:${seconds.padStart(
      2,
      "0"
    )} ${convertedPeriod}`;

    setConvertedTime(formattedTime);
  };

  return (
    <div>
      <div>
        <label>Hours:</label>
        <input
          type="number"
          value={hours}
          onChange={(e) => setHours(e.target.value)}
        />
      </div>
      <div>
        <label>Minutes:</label>
        <input
          type="number"
          value={minutes}
          onChange={(e) => setMinutes(e.target.value)}
        />
      </div>
      <div>
        <label>Seconds:</label>
        <input
          type="number"
          value={seconds}
          onChange={(e) => setSeconds(e.target.value)}
        />
      </div>
      <div>
        <label>AM/PM:</label>
        <select value={period} onChange={(e) => setPeriod(e.target.value)}>
          <option value="AM">AM</option>
          <option value="PM">PM</option>
        </select>
      </div>
      <button onClick={convertTime}>Convert</button>
      {convertedTime && <div>Converted Time: {convertedTime}</div>}
    </div>
  );
};

export default IndianToHongKongTimeConverter;
