import React, { useState, useEffect } from "react";
import "./style.css";
import { GrFormNextLink } from "react-icons/gr";
// import moment, { tz } from "moment";
// import "moment-timezone";
import { DateTime } from "luxon";
const CustomInput = () => {
  const [indianTime, setIndianTime] = useState("");
  const [hongKongTimestate, setHongKongTime] = useState("");
  const [UsTimestate, setUsTime] = useState("");

  const convertTime = () => {
    const indianDateTime = DateTime.fromFormat(indianTime, "HH:mm", {
      zone: "Asia/Kolkata",
    });
    if (!indianDateTime.isValid) {
      setHongKongTime("Invalid Time");
      setUsTime("  Time");
      return;
    }

    const hongKongDateTime = indianDateTime.setZone("Asia/Hong_Kong");
    const formattedHongKongTime = hongKongDateTime.toFormat("hh:mm:ss a");

    setHongKongTime(formattedHongKongTime);

    const usTime = indianDateTime.setZone("America/New_York");
    const formattedusTime = usTime.toFormat("hh:mm:ss a");

    setUsTime(formattedusTime);
    console.log(hongKongTimestate, "  ", UsTimestate);
  };

  const [hours, setHours] = useState("");
  const [minutes, setMinutes] = useState("");
  const [seconds, setSeconds] = useState("");
  const [amPm, setAmPm] = useState("AM");

  const handleTimeSubmit = () => {
    const data = {
      hours: parseInt(hours),
      minutes: parseInt(minutes),
      seconds: parseInt(seconds),
      am_pm: amPm,
    };

    fetch("/api/save_custom_time", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
        // Handle success or display a notification to the user
      })
      .catch((error) => {
        console.error("Error:", error);
        // Handle error or display a notification to the user
      });
  };

  return (
    <div className="input-wrapper-container">
      <div className="custom-time-input">
        <h2>Custom Indian Time Input</h2>
        <div>
          <label>Hours:</label>
          <input
            type="number"
            value={hours}
            onChange={(e) => setHours(e.target.value)}
          />
        </div>
        <div>
          <label>Minutes:</label>
          <input
            type="number"
            value={minutes}
            onChange={(e) => setMinutes(e.target.value)}
          />
        </div>
        <div>
          <label>Seconds:</label>
          <input
            type="number"
            value={seconds}
            onChange={(e) => setSeconds(e.target.value)}
          />
        </div>
        <div>
          <label>AM/PM:</label>
          <select value={amPm} onChange={(e) => setAmPm(e.target.value)}>
            <option value="AM">AM</option>
            <option value="PM">PM</option>
          </select>
        </div>
        <button onClick={handleTimeSubmit}>Submit</button>
      </div>
    </div>
  );
};

export default CustomInput;
