import React, { useState, useEffect } from "react";
import "./style.css";
import { GrFormNextLink } from "react-icons/gr";
// import moment, { tz } from "moment";
// import "moment-timezone";
import { DateTime } from "luxon";
const CustomInput = () => {
  const [hours, setHours] = useState("");
  const [minutes, setMinutes] = useState("");
  const [seconds, setSeconds] = useState("");
  const [period, setPeriod] = useState("AM");
  const [convertedTime, setConvertedTime] = useState("");

  const convertTime = () => {
    const indianTime = moment.tz(
      `${hours}:${minutes}:${seconds} ${period}`,
      "HH:mm:ss A",
      "Asia/Kolkata"
    );
    const hongKongTime = indianTime.clone().tz("Asia/Hong_Kong");
    const formattedTime = hongKongTime.format("hh:mm:ss A");

    setConvertedTime(formattedTime);
  };

  const [amPm, setAmPm] = useState("AM");

  const handleTimeSubmit = () => {
    const data = {
      hours: parseInt(hours),
      minutes: parseInt(minutes),
      seconds: parseInt(seconds),
      am_pm: amPm,
    };

    fetch("/api/save_custom_time", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
        // Handle success or display a notification to the user
      })
      .catch((error) => {
        console.error("Error:", error);
        // Handle error or display a notification to the user
      });
  };

  return (
    <div className="input-wrapper-container">
      <div>
        <label>Hours:</label>
        <input
          type="number"
          value={hours}
          onChange={(e) => setHours(e.target.value)}
        />
      </div>
      <div>
        <label>Minutes:</label>
        <input
          type="number"
          value={minutes}
          onChange={(e) => setMinutes(e.target.value)}
        />
      </div>
      <div>
        <label>Seconds:</label>
        <input
          type="number"
          value={seconds}
          onChange={(e) => setSeconds(e.target.value)}
        />
      </div>
      <div>
        <label>AM/PM:</label>
        <select value={period} onChange={(e) => setPeriod(e.target.value)}>
          <option value="AM">AM</option>
          <option value="PM">PM</option>
        </select>
      </div>
      <button onClick={convertTime}>Convert</button>
      {convertedTime && <div>Converted Time: {convertedTime}</div>}
    </div>
  );
};

export default CustomInput;
