import React, { useState } from "react";
import moment from "moment-timezone";

const IndianToHongKongTimeConverter = () => {
  const [indianTime, setIndianTime] = useState("");
  const [convertedTime, setConvertedTime] = useState("");

  const convertTime = () => {
    const hongKongTime = moment
      .tz(indianTime, "HH:mm:ss", "Asia/Kolkata")
      .tz("Asia/Hong_Kong");
    const formattedTime = hongKongTime.format("hh:mm:ss A");

    setConvertedTime(formattedTime);
  };

  return (
    <div>
      <div>
        <label>Indian Time:</label>
        <input
          type="text"
          value={indianTime}
          onChange={(e) => setIndianTime(e.target.value)}
        />
      </div>
      <button onClick={convertTime}>Convert</button>
      {convertedTime && <div>Converted Time: {convertedTime}</div>}
    </div>
  );
};

export default IndianToHongKongTimeConverter;
