import React, { useState } from "react";
import "./style.css";
import { GrFormNextLink } from "react-icons/gr";

const CustomInput = () => {
  const [hours, setHours] = useState("");
  const [minutes, setMinutes] = useState("");
  const [seconds, setSeconds] = useState("");
  const [amPm, setAmPm] = useState("AM");

  const handleTimeSubmit = () => {
    const [indianTime, setIndianTime] = useState("");
    const [hongKongTime, setHongKongTime] = useState("");
    const [usTime, setUsTime] = useState("");

    const handleIndianTimeChange = (event) => {
      const { value } = event.target;
      setIndianTime(value);

      const indianTimestamp = Date.parse(`01 Jan 1970 ${value} GMT+0530`);
      const hongKongTimestamp = indianTimestamp + 2.5 * 60 * 60 * 1000;
      const usTimestamp = indianTimestamp - 9.5 * 60 * 60 * 1000;

      setHongKongTime(new Date(hongKongTimestamp).toLocaleTimeString("en-HK"));
      setUsTime(new Date(usTimestamp).toLocaleTimeString("en-US"));
    };
    const data = {
      hours: parseInt(hours),
      minutes: parseInt(minutes),
      seconds: parseInt(seconds),
      am_pm: amPm,
    };

    fetch("/api/save_custom_time", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
        // Handle success or display a notification to the user
      })
      .catch((error) => {
        console.error("Error:", error);
        // Handle error or display a notification to the user
      });
  };

  return (
    <div className="input-wrapper-container">
      <div className="input-wrapper">
        <input
          placeholder="Custom Time"
          type="text"
          value={hours}
          onChange={(e) => setHours(e.target.value)}
        />

        <GrFormNextLink size={30} cursor={"pointer"} />
      </div>
    </div>
  );
};

export default CustomInput;
