import React, { useState, useEffect } from "react";

function Clock() {
  const [customHour, setCustomHour] = useState("");
  const [customMinute, setCustomMinute] = useState("");
  const [customSecond, setCustomSecond] = useState("");
  const [customAmPm, setCustomAmPm] = useState("");
  const [hongKongTime, setHongKongTime] = useState("");
  const [usTime, setUSTime] = useState("");

  useEffect(() => {
    const interval = setInterval(() => {
      getCurrentTime();
    }, 1000);

    return () => {
      clearInterval(interval);
    };
  }, []);

  const getCurrentTime = () => {
    const now = new Date();

    // Hong Kong Time
    const hongKongOffset = 8;
    const hongKongDate = new Date(
      now.getTime() + hongKongOffset * 60 * 60 * 1000
    );
    const hongKongTimeString = formatTime(hongKongDate);

    // US Time
    const usOffset = -4;
    const usDate = new Date(now.getTime() + usOffset * 60 * 60 * 1000);
    const usTimeString = formatTime(usDate);

    setHongKongTime(hongKongTimeString);
    setUSTime(usTimeString);
  };

  const formatTime = (date) => {
    let hours = date.getHours();
    let minutes = date.getMinutes();
    let seconds = date.getSeconds();

    if (customHour !== "") {
      hours = parseInt(customHour);
    }
    if (customMinute !== "") {
      minutes = parseInt(customMinute);
    }
    if (customSecond !== "") {
      seconds = parseInt(customSecond);
    }

    if (customAmPm !== "") {
      const isPm = customAmPm.toLowerCase() === "pm";
      if (isPm && hours !== 12) {
        hours += 12;
      } else if (!isPm && hours === 12) {
        hours = 0;
      }
    }

    const currentDateTime = new Date();
    const adjustedTime = new Date(currentDateTime);
    adjustedTime.setHours(hours);
    adjustedTime.setMinutes(minutes);
    adjustedTime.setSeconds(seconds);

    const formattedHours = String(adjustedTime.getHours() % 12 || 12).padStart(
      2,
      "0"
    );
    const formattedMinutes = String(adjustedTime.getMinutes()).padStart(2, "0");
    const formattedSeconds = String(adjustedTime.getSeconds()).padStart(2, "0");
    const amPm = adjustedTime.getHours() >= 12 ? "PM" : "AM";

    return `${formattedHours}:${formattedMinutes}:${formattedSeconds} ${amPm}`;
  };

  const handleCustomTimeSubmit = (event) => {
    event.preventDefault();
    getCurrentTime();
  };

  return (
    <div>
      <h2>Hong Kong Time: {hongKongTime}</h2>
      <h2>US Time: {usTime}</h2>
      <form onSubmit={handleCustomTimeSubmit}>
        <label>
          Custom Time:
          <input
            type="number"
            value={customHour}
            min="1"
            max="12"
            placeholder="Hour"
            onChange={(e) => setCustomHour(e.target.value)}
            required
          />
          <input
            type="number"
            value={customMinute}
            min="0"
            max="59"
            placeholder="Minute"
            onChange={(e) => setCustomMinute(e.target.value)}
            required
          />
          <input
            type="number"
            value={customSecond}
            min="0"
            max="59"
            placeholder="Second"
            onChange={(e) => setCustomSecond(e.target.value)}
            required
          />
          <select
            value={customAmPm}
            onChange={(e) => setCustomAmPm(e.target.value)}
            required
          >
            <option value="">AM/PM</option>
            <option value="AM">AM</option>
            <option value="PM">PM</option>
          </select>
        </label>
        <button type="submit">Update Time</button>
      </form>
    </div>
  );
}

export default Clock;
